var btn=document.querySelector(".open-popup"),popup=document.querySelector(".popup"),close=popup.querySelector(".esc"),form=popup.querySelector("form"),name=popup.querySelector("[name=name]"),email=popup.querySelector("[name=e-mail]"),text=popup.querySelector("[name=text]");btn.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("popup-show")}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("popup-show")}),form.addEventListener("submit",function(a){name.value&&email.value&&text.value||a.preventDefault()});
